mapper.insert:
insert into SC_COMMENT (ID, TYPE, CONTENT, 
  CONTENT_TIME, SCORE, ISHIDDEN, 
  PARENT_ID, COMMENT_PEOPLE, FLAG
  )
values (#{id,jdbcType=VARCHAR}, #{type,jdbcType=CHAR}, #{content,jdbcType=VARCHAR}, 
  #{contentTime,jdbcType=TIMESTAMP}, #{score,jdbcType=DECIMAL}, #{ishidden,jdbcType=CHAR}, 
  #{parentId,jdbcType=VARCHAR}, #{commentPeople,jdbcType=VARCHAR}, #{flag,jdbcType=CHAR}
  )

mapper.select:
    select 
    
    from sc_comment a,sc_comments_relation b where a.id=b.comments_id
    where a.flag=1 and b.flag=a.flag
	
		b.ref_id=#{map.contactid,jdbcType=VARCHAR}
	
	
		a.COMMENT_PEOPLE=#{map.userid,jdbcType=VARCHAR}
	
	
		b.type=#{map.type,jdbcType=VARCHAR}
