mapper.select:
    select 
    
    from (
	select * from SC_EVENTSINFO where isuse=1 and flag=1
	union
	select * from SC_EVENTSINFO where isuse is null and starttime is not null and endtime is not null and flag=1
	)
    where 1=1
    
    	and NAME=#{map.event_name,jdbcType=VARCHAR}
    
    
    	and ID=#{map.event_id,jdbcType=VARCHAR}
    
    
    	and ENDTIME>=#{map.start_time,jdbcType=TIMESTAMP,typeHandler=cn.sqhl.shop.core.DateTypeHandler}
    
    
    	and STARTTIME<=#{map.end_time,jdbcType=TIMESTAMP,typeHandler=cn.sqhl.shop.core.DateTypeHandler}
